// Helper to fetch the database structure
async function fetchDatabaseStructure(databaseId, apiKey) {
   // Headers for Notion API requests
   const headers = {
    Authorization: `Bearer ${apiKey}`,
    "Content-Type": "application/json",
    "Notion-Version": "2022-06-28", // or the version you are using
  };
  try {
    const response = await fetch(
      `https://api.notion.com/v1/databases/${databaseId}`,
      {
        method: "GET",
        headers: headers,
      }
    );

    if (!response.ok) {
      throw new Error(
        `Error fetching database structure: ${response.statusText}`
      );
    }

    const data = await response.json();
    console.log(JSON.stringify(data));
    return data;
  } catch (error) {
    throw new Error(`Failed to fetch database structure: ${error.message}`);
  }
}

async function get_notion_db_structure(params, userSettings) {
  const { databaseId } = params;
  const { notionSecretKey } = userSettings;

  if (!notionSecretKey) {
    throw new Error(
      "Missing the Notion API Key. Please set it in the plugin settings."
    );
  }

  if (!databaseId) {
    throw new Error(
      "Missing the Database Id. Please set it in the plugin settings."
    );
  }

  try {
    const result = await fetchDatabaseStructure(databaseId,notionSecretKey);
    return result;
  } catch (error) {
    console.error("Error:", error.message); 
  }
}
